game.onUpdate(function () {
    scene.centerCameraAt(Joe.x, Joe.y)
    scroller.setBackgroundScrollOffset(scene.cameraProperty(CameraProperty.X) * -0.8, scene.cameraProperty(CameraProperty.Y) * -0.7, scroller.BackgroundLayer.Layer1)
    scroller.setBackgroundScrollOffset(scene.cameraProperty(CameraProperty.X) * -0.4, scene.cameraProperty(CameraProperty.Y) * -0.35, scroller.BackgroundLayer.Layer0)
    if (Joe.vy == 0) {
        if (controller.right.isPressed()) {
            Joe.vx += 60
        }
        if (controller.left.isPressed()) {
            Joe.vx += -60
        }
        Joe.vx = Joe.vx * 0.8
        if (controller.A.isPressed()) {
            Joe.vy = -250
        }
    } else {
        if (controller.right.isPressed()) {
            Joe.vx += 2
        }
        if (controller.left.isPressed()) {
            Joe.vx += -2
        }
    }
    if (Joe.isHittingTile(CollisionDirection.Left)) {
        Joe.vx = 0
        if (controller.A.isPressed()) {
            Joe.vy = -200
            Joe.vx = 100
        }
        if (Joe.tileKindAt(TileDirection.Left, assets.tile`myTile24`)) {
            Joe.vx = 600
        }
    }
    if (Joe.isHittingTile(CollisionDirection.Right)) {
        Joe.vx = 0
        if (controller.A.isPressed()) {
            Joe.vy = -200
            Joe.vx = -100
        }
        if (Joe.tileKindAt(TileDirection.Right, assets.tile`myTile24`)) {
            Joe.vx = -400
        }
    }
    if (Joe.isHittingTile(CollisionDirection.Top)) {
        Joe.vy = 0
        if (Joe.tileKindAt(TileDirection.Top, assets.tile`myTile23`)) {
            Joe.vy = -600
        }
    }
    if (Joe.isHittingTile(CollisionDirection.Bottom)) {
        if (Joe.tileKindAt(TileDirection.Bottom, assets.tile`myTile23`)) {
            Joe.vy = -400
        }
    }
    Joe_sprite()
})
